source('utils.m');global EXPONENTIAL;global TANH;global LINEAR;global RELU;[EXPONENTIAL, TANH, LINEAR ,RELU]  = enum();function result = activation(type, input)  global EXPONENTIAL;  global TANH;  global LINEAR;  global RELU;  switch(type)    case(EXPONENTIAL)       result = 1 ./ (1 + exp(-input));     case(TANH)        result = tanh(input);     case(RELU)        result = max(0,input);        otherwise        result = input;  endswitchendfunctionfunction f_prime = activation_prime(X, type)  global EXPONENTIAL;  global TANH;  global LINEAR;  global RELU;  switch(type)    case(EXPONENTIAL)       f_prime = (X- (X.*X));     case(TANH)       f_prime = 1 - (X .* X);     case(RELU)       f_prime =  ifelse(X > 0, 1, 0);       otherwise       f_prime = ones(rows(X), columns(X));  endswitchendfunction